Repository Implementation
// repositories/base.repository.js

export class BaseRepository {
  constructor(model) {
    this.model = model;
  }

  async findAll(query = {}, options = {}) {
    const { page = 1, limit = 20, sort = '-createdAt' } = options;
    const skip = (page - 1) * limit;

    return this.model
      .find(query)
      .sort(sort)
      .skip(skip)
      .limit(limit)
      .lean();
  }

  async findById(id) {
    return this.model.findById(id).lean();
  }

  async findOne(query) {
    return this.model.findOne(query).lean();
  }

  async create(data) {
    const doc = new this.model(data);
    await doc.save();
    return doc.toObject();
  }

  async update(id, data) {
    return this.model
      .findByIdAndUpdate(id, data, { new: true })
      .lean();
  }

  async delete(id) {
    return this.model.findByIdAndDelete(id);
  }

  async count(query = {}) {
    return this.model.countDocuments(query);
  }
}

Specialized Repository
// repositories/products.repository.js

import { BaseRepository } from './base.repository.js';
import { Product } from '../models/Product.js';

export class ProductRepository extends BaseRepository {
  constructor() {
    super(Product);
  }

  async findBySku(sku) {
    return this.model.findOne({ sku }).lean();
  }

  async findByCategory(categoryId, options = {}) {
    return this.findAll({ category: categoryId }, options);
  }

  async findInStock() {
    return this.model.find({ stock: { $gt: 0 } }).lean();
  }

  async updateStock(id, quantity) {
    return this.model.findByIdAndUpdate(
      id,
      { $inc: { stock: quantity } },
      { new: true }
    ).lean();
  }

  async searchByName(searchTerm, options = {}) {
    const query = {
      name: { $regex: searchTerm, $options: 'i' }
    };
    return this.findAll(query, options);
  }

  async aggregateByCategory() {
    return this.model.aggregate([
      { $group: {
          _id: '$category',
          count: { $sum: 1 },
          avgPrice: { $avg: '$price' }
        }
      }
    ]);
  }
}

