            type="text"
            value={newTaskTitle}
            onChange={(e) =&gt; setNewTaskTitle(e.target.value)}
            placeholder="Enter a new task..."
            style={{ padding: '8px', marginRight: '10px', width: '200px' }}
          /&gt;
          &lt;button onClick={addTask}&gt;
            Add Task
          &lt;/button&gt;
        &lt;/div&gt;

        {/* Task List */}
        {tasks.map(task =&gt; (
          &lt;TaskItem
            key={task.id}
            title={task.title}
            completed={task.completed}
            onToggle={() =&gt; toggleTask(task.id)}
          /&gt;
        ))}
      &lt;/div&gt;
    &lt;/div&gt;
  )
}

