// frontend/src/App.jsx
import { useState, useEffect } from 'react'
import axios from 'axios'
import './App.css'

const API_URL = import.meta.env.VITE_API_URL || 'http://localhost:4000'

function App() {
  const [items, setItems] = useState([])
  const [newItem, setNewItem] = useState('')
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  useEffect(() =&gt; {
    fetchItems()
  }, [])

  const fetchItems = async () =&gt; {
    try {
      setLoading(true)
      const response = await axios.get(`${API_URL}/api/items`)
      setItems(response.data)
      setError(null)
    } catch (err) {
      setError('Failed to fetch items')
      console.error(err)
    } finally {
      setLoading(false)
    }
  }

  const addItem = async (e) =&gt; {
    e.preventDefault()
    if (!newItem.trim()) return

    try {
      const response = await axios.post(`${API_URL}/api/items`, {
        name: newItem
      })
      setItems([response.data, ...items])
      setNewItem('')
    } catch (err) {
      setError('Failed to add item')
    }
  }

  const toggleItem = async (id, completed) =&gt; {
    try {
      const response = await axios.put(`${API_URL}/api/items/${id}`, {
        completed: !completed
      })
      setItems(items.map(item =&gt;
        item._id === id ? response.data : item
      ))
    } catch (err) {
      setError('Failed to update item')
    }
  }

  const deleteItem = async (id) =&gt; {
    try {
      await axios.delete(`${API_URL}/api/items/${id}`)
      setItems(items.filter(item =&gt; item._id !== id))
    } catch (err) {
      setError('Failed to delete item')
    }
  }

  return (
    &lt;div className="app"&gt;
      &lt;h1&gt;MERN Stack Items&lt;/h1&gt;

      {error && &lt;div className="error"&gt;{error}&lt;/div&gt;}

      &lt;form onSubmit={addItem}&gt;
        &lt;input
          type="text"
          value={newItem}
          onChange={(e) =&gt; setNewItem(e.target.value)}
          placeholder="Add new item..."
        /&gt;
        &lt;button type="submit"&gt;Add&lt;/button&gt;
      &lt;/form&gt;

      {loading ? (
        &lt;p&gt;Loading...&lt;/p&gt;
      ) : (
        &lt;ul&gt;
          {items.map(item =&gt; (
            &lt;li key={item._id} className={item.completed ? 'completed' : ''}&gt;
              &lt;span onClick={() =&gt; toggleItem(item._id, item.completed)}&gt;
                {item.name}
              &lt;/span&gt;
              &lt;button onClick={() =&gt; deleteItem(item._id)}&gt;Delete&lt;/button&gt;
            &lt;/li&gt;
          ))}
        &lt;/ul&gt;
      )}
    &lt;/div&gt;
  )
}

export default App
